import numpy as np 
import pandas as pd 
import matplotlib.pyplot as plt

path = r"https://drive.google.com/uc?export=download&id=1xxDtrZKfuWQfl-6KA9XEd_eatitNPnkB" 
df = pd.read_csv(path)

df.head( )

X= df['total_sqft_int']
y= df['price']

"""To estimate the intercept ùú∑0, we can use the following formula:
y = Œ≤0 + Œ≤1x![1_FHxzIyZEgc3zI0erQub_-w.png](data:image/png;base64,)
"""

def linear_regression(X, y): 
  N = len(X)
  X_mean = X.mean()
  y_mean = y.mean()
  
  B1_num = ((X - X_mean) * (y - y_mean)).sum()
  B1_den = ((X - X_mean)**2).sum()
  B1 = B1_num / B1_den 
  
  BO = y_mean - (B1*X_mean)

  reg_line = 'y = {} + {}Œ≤'.format(BO, round(B1, 3))
  return (BO, B1, reg_line)

from google.colab import drive
drive.mount('/content/drive')

print(linear_regression(X, y))

"""Below is the formula for Pearson‚Äôs correlation coefficient:![1_EKTdjU5ls19Jr2KNyCAp1w.png](data:image/png;base64,)"""

def corr_coef(X, y):
    N = len(X)
    
    num = (N * (X*y).sum()) - (X.sum() * y.sum())
    den = np.sqrt((N * (X**2).sum() - X.sum()**2) * (N * (y**2).sum() - y.sum()**2))
    R = num / den
    return R

B0, B1, reg_line = linear_regression(X, y)
print("Regression Line", reg_line)
R = corr_coef(X, y)
print('Correlation Coefe. :', R)
print('"Good of score" : ', R**2)
